mapper.select:
SELECT
,yuangong.yuangong_name as yuangongName ,yuangong.yuangong_phone as yuangongPhone ,yuangong.yuangong_id_number as yuangongIdNumber ,yuangong.yuangong_photo as yuangongPhoto ,yuangong.bumen_types as bumenTypes ,yuangong.zhiwei_types as zhiweiTypes ,yuangong.yuangong_email as yuangongEmail ,yuangong.xueli_types as xueliTypes ,yuangong.yuangong_jiaoyu_content as yuangongJiaoyuContent ,yuangong.yuangong_gongzuo_content as yuangongGongzuoContent ,yuangong.shangpin_shixi_content as shangpinShixiContent ,yuangong.shangpin_rongu_content as shangpinRonguContent ,yuangong.jinyong_types as jinyongTypes
FROM jixiao a left JOIN yuangong yuangong ON a.yuangong_id = yuangong.id
and a.id in
#{item}
and ( a.yuangong_id = #{params.yuangongId} )
and a.jixiao_uuid_number like CONCAT('%',#{params.jixiaoUuidNumber},'%')
and a.jixiao_name like CONCAT('%',#{params.jixiaoName},'%')
and a.jixiao_types = #{params.jixiaoTypes}
and a.jixiao_dafen >= #{params.jixiaoDafenStart}
and a.jixiao_dafen <= #{params.jixiaoDafenEnd}
and a.jixiao_dafen = #{params.jixiaoDafen}
and UNIX_TIMESTAMP(a.pinggu_time) >= UNIX_TIMESTAMP(#{params.pingguTimeStart})
and UNIX_TIMESTAMP(a.pinggu_time) <= UNIX_TIMESTAMP(#{params.pingguTimeEnd})
and a.pinggu_time = #{params.pingguTime}
and a.jixiao_content like CONCAT('%',#{params.jixiaoContent},'%')
and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart})
and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd})
and a.insert_time = #{params.insertTime}
and a.yuangong_id IS NOT NULL
and yuangong.yuangong_name like CONCAT('%',#{params.yuangongName},'%')
and yuangong.yuangong_phone like CONCAT('%',#{params.yuangongPhone},'%')
and yuangong.yuangong_id_number like CONCAT('%',#{params.yuangongIdNumber},'%')
and yuangong.bumen_types = #{params.bumenTypes}
and yuangong.zhiwei_types = #{params.zhiweiTypes}
and yuangong.yuangong_email like CONCAT('%',#{params.yuangongEmail},'%')
and yuangong.xueli_types = #{params.xueliTypes}
and yuangong.yuangong_jiaoyu_content like CONCAT('%',#{params.yuangongJiaoyuContent},'%')
and yuangong.yuangong_gongzuo_content like CONCAT('%',#{params.yuangongGongzuoContent},'%')
and yuangong.shangpin_shixi_content like CONCAT('%',#{params.shangpinShixiContent},'%')
and yuangong.shangpin_rongu_content like CONCAT('%',#{params.shangpinRonguContent},'%')
and yuangong.jinyong_types = #{params.jinyongTypes}
order by a.${params.sort} ${params.order}
