mapper.select:
SELECT



FROM yuangong  a



        and a.id in

        #{item}



        and a.username like CONCAT('%',#{params.username},'%')


        and a.password like CONCAT('%',#{params.password},'%')


        and a.yuangong_name like CONCAT('%',#{params.yuangongName},'%')


        and a.yuangong_phone like CONCAT('%',#{params.yuangongPhone},'%')


        and a.yuangong_id_number like CONCAT('%',#{params.yuangongIdNumber},'%')


        and a.sex_types = #{params.sexTypes}


        and a.bumen_types = #{params.bumenTypes}


        and a.zhiwei_types = #{params.zhiweiTypes}


        and a.yuangong_email like CONCAT('%',#{params.yuangongEmail},'%')


        and a.xueli_types = #{params.xueliTypes}


        and a.yuangong_jiaoyu_content like CONCAT('%',#{params.yuangongJiaoyuContent},'%')


        and a.yuangong_gongzuo_content like CONCAT('%',#{params.yuangongGongzuoContent},'%')


        and a.shangpin_shixi_content like CONCAT('%',#{params.shangpinShixiContent},'%')


        and a.shangpin_rongu_content like CONCAT('%',#{params.shangpinRonguContent},'%')


        and a.jinyong_types = #{params.jinyongTypes}



order by a.${params.sort} ${params.order}
