mapper.select:
SELECT



FROM yuangong_kaoqin  a



        and a.id in

        #{item}



        and a.yuangong_kaoqin_uuid_number like CONCAT('%',#{params.yuangongKaoqinUuidNumber},'%')


        and a.yuangong_kaoqin_name like CONCAT('%',#{params.yuangongKaoqinName},'%')


        and a.yuangong_kaoqin_types = #{params.yuangongKaoqinTypes}


        and a.bumen_types = #{params.bumenTypes}


        and a.yuangong_kaoqin_content like CONCAT('%',#{params.yuangongKaoqinContent},'%')


          and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


          and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 


        and a.insert_time = #{params.insertTime}


          and UNIX_TIMESTAMP(a.jiezhi_time) >= UNIX_TIMESTAMP(#{params.jiezhiTimeStart}) 


          and UNIX_TIMESTAMP(a.jiezhi_time) <= UNIX_TIMESTAMP(#{params.jiezhiTimeEnd}) 


        and a.jiezhi_time = #{params.jiezhiTime}



order by a.${params.sort} ${params.order}
