mapper.select:
SELECT
,yuangong.yuangong_name as yuangongName ,yuangong.yuangong_phone as yuangongPhone ,yuangong.yuangong_id_number as yuangongIdNumber ,yuangong.yuangong_photo as yuangongPhoto ,yuangong.bumen_types as bumenTypes ,yuangong.zhiwei_types as zhiweiTypes ,yuangong.yuangong_email as yuangongEmail ,yuangong.xueli_types as xueliTypes ,yuangong.yuangong_jiaoyu_content as yuangongJiaoyuContent ,yuangong.yuangong_gongzuo_content as yuangongGongzuoContent ,yuangong.shangpin_shixi_content as shangpinShixiContent ,yuangong.shangpin_rongu_content as shangpinRonguContent ,yuangong.jinyong_types as jinyongTypes ,yuangongKaoqin.yuangong_kaoqin_uuid_number as yuangongKaoqinUuidNumber ,yuangongKaoqin.yuangong_kaoqin_name as yuangongKaoqinName ,yuangongKaoqin.yuangong_kaoqin_types as yuangongKaoqinTypes ,yuangongKaoqin.bumen_types as bumenTypes ,yuangongKaoqin.yuangong_kaoqin_content as yuangongKaoqinContent ,yuangongKaoqin.jiezhi_time as jiezhiTime
FROM yuangong_kaoqin_list a left JOIN yuangong yuangong ON a.yuangong_id = yuangong.id left JOIN yuangong_kaoqin yuangongKaoqin ON a.yuangong_kaoqin_id = yuangongKaoqin.id
and a.id in
#{item}
and ( a.yuangong_id = #{params.yuangongId} )
and ( a.yuangong_kaoqin_id = #{params.yuangongKaoqinId} )
and a.yuangong_kaoqin_list_types = #{params.yuangongKaoqinListTypes}
and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart})
and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd})
and a.insert_time = #{params.insertTime}
and UNIX_TIMESTAMP(a.update_time) >= UNIX_TIMESTAMP(#{params.updateTimeStart})
and UNIX_TIMESTAMP(a.update_time) <= UNIX_TIMESTAMP(#{params.updateTimeEnd})
and a.update_time = #{params.updateTime}
and a.yuangong_id IS NOT NULL
and yuangong.yuangong_name like CONCAT('%',#{params.yuangongName},'%')
and yuangong.yuangong_phone like CONCAT('%',#{params.yuangongPhone},'%')
and yuangong.yuangong_id_number like CONCAT('%',#{params.yuangongIdNumber},'%')
and yuangong.bumen_types = #{params.bumenTypes}
and yuangong.zhiwei_types = #{params.zhiweiTypes}
and yuangong.yuangong_email like CONCAT('%',#{params.yuangongEmail},'%')
and yuangong.xueli_types = #{params.xueliTypes}
and yuangong.yuangong_jiaoyu_content like CONCAT('%',#{params.yuangongJiaoyuContent},'%')
and yuangong.yuangong_gongzuo_content like CONCAT('%',#{params.yuangongGongzuoContent},'%')
and yuangong.shangpin_shixi_content like CONCAT('%',#{params.shangpinShixiContent},'%')
and yuangong.shangpin_rongu_content like CONCAT('%',#{params.shangpinRonguContent},'%')
and yuangong.jinyong_types = #{params.jinyongTypes}
and a.yuangong_kaoqin_id IS NOT NULL
and yuangongKaoqin.yuangong_kaoqin_uuid_number like CONCAT('%',#{params.yuangongKaoqinUuidNumber},'%')
and yuangongKaoqin.yuangong_kaoqin_name like CONCAT('%',#{params.yuangongKaoqinName},'%')
and yuangongKaoqin.yuangong_kaoqin_types = #{params.yuangongKaoqinTypes}
and yuangongKaoqin.bumen_types = #{params.bumenTypes}
and yuangongKaoqin.yuangong_kaoqin_content like CONCAT('%',#{params.yuangongKaoqinContent},'%')
and UNIX_TIMESTAMP(yuangongKaoqin.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart})
and UNIX_TIMESTAMP(yuangongKaoqin.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd})
and a.insert_time = #{params.insertTime}
and UNIX_TIMESTAMP(yuangongKaoqin.jiezhi_time) >= UNIX_TIMESTAMP(#{params.jiezhiTimeStart})
and UNIX_TIMESTAMP(yuangongKaoqin.jiezhi_time) <= UNIX_TIMESTAMP(#{params.jiezhiTimeEnd})
and a.jiezhi_time = #{params.jiezhiTime}
order by a.${params.sort} ${params.order}
